import React, { useState } from "react";

const Likes = () => {
  const [likedPosts, setLikedPosts] = useState([
    {
      title: "Post 1",
      description: "This is a description of the first post.",
      image: "https://via.placeholder.com/300?text=Post+1",
    },
    {
      title: "Post 2",
      description: "This is a description of the second post.",
      image: "https://via.placeholder.com/300?text=Post+2",
    },
    {
      title: "Post 3",
      description: "This is a description of the third post.",
      image: "https://via.placeholder.com/300?text=Post+3",
    },
    // Add more posts
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);

  const openModal = (post) => {
    setSelectedPost(post);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-6">
      {/* Header */}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Liked Posts</h1>
        <p className="mt-2 text-lg">
          Explore all the posts you've liked. Keep track of your favorite
          content.
        </p>
      </div>

      {/* Liked Posts Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8">
        {likedPosts.map((post, index) => (
          <div
            key={index}
            className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition duration-200 hover:cursor-pointer"
            onClick={() => openModal(post)}
          >
            <img
              src={post.image}
              alt={post.title}
              className="w-full h-72 object-cover rounded-md"
            />
            <div className="mt-4">
              <h3 className="text-xl font-semibold text-gray-800">
                {post.title}
              </h3>
              <p className="text-gray-600 mt-2">{post.description}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for Post Details */}
      {isModalOpen && selectedPost && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-75 z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-1/2">
            <h3 className="text-2xl font-semibold">{selectedPost.title}</h3>
            <p className="text-gray-600 mt-2">{selectedPost.description}</p>
            <img
              src={selectedPost.image}
              alt={selectedPost.title}
              className="w-full h-96 mt-4 rounded-md"
            />
            <button
              className="mt-6 bg-blue-500 text-white py-2 px-6 rounded-md hover:bg-blue-600"
              onClick={closeModal}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Likes;
