import React, { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart,
  faComment,
  faShareSquare,
} from "@fortawesome/free-regular-svg-icons";
import vividlyContext from "../Context/vividlyContext";
import { useNavigate } from "react-router-dom";

import { faHeart as LikeHeart } from "@fortawesome/free-solid-svg-icons";

const Explore = () => {
  const navigate = useNavigate();
  const context = useContext(vividlyContext);
  const { searchUserFunc, searchUsers, handleFetchTrendingPosts } = context;

  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;

  const [keyword, setKeyword] = useState("");
  const [show, setShow] = useState(true);

  const [posts, setPosts] = useState([
    {
      id: 1,
      user: "John Doe",
      postImage: "https://via.placeholder.com/400x400?text=Post+1",
      description: "Exploring the beauty of nature!",
      likes: 100,
      comments: 30,
      shares: 12,
    },
    {
      id: 2,
      user: "Jane Smith",
      postImage: "https://via.placeholder.com/400x400?text=Post+2",
      description: "A new tech gadget I love!",
      likes: 85,
      comments: 18,
      shares: 7,
    },
    {
      id: 3,
      user: "Mark Johnson",
      postImage: "https://via.placeholder.com/400x400?text=Post+3",
      description: "Fitness journey - day 1!",
      likes: 50,
      comments: 12,
      shares: 3,
    },
    // Add more posts here...
  ]);
  const handleOnchange = (e) => {
    const searchTerm = e.target.value;
    setKeyword(e.target.value);
    if (searchTerm.length > 0) {
      searchUserFunc(e.target.value);
      setShow(false);
    } else {
      setShow(true);
    }
  };

  const handleFetchSearchedUData = async (id) => {
    localStorage.setItem("sUserId", JSON.stringify(id));
    navigate("/searchUser");
  };
  useEffect(() => {
    trendingPostDetailsFunc();
  }, []);

  const handleFetchPostLikes = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostLikes/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postLikes.likes;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const handleLikePost = async (postId) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);
    const data = await fetch(`${ENDPOINT}/api/post/likePost/${postId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token": parsedToken,
      },
    });
    const response = await data.json();
    const userId = localStorage.getItem("id");
    const parsedUserId = JSON.parse(userId);

    const post = tPostDetails.find((post) => post._id === postId);
    if (post) {
      const userAlreadyLiked = post.likes.some(
        (like) => like.user === parsedUserId
      );

      if (userAlreadyLiked) {
        setTPostDetails(
          tPostDetails.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: p.likes.filter((like) => like.user !== parsedUserId),
                  likeStatus: "Not Liked",
                }
              : p
          )
        );
      } else {
        setTPostDetails(
          tPostDetails.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: [...p.likes, { user: parsedUserId }],
                  likeStatus: "Liked",
                }
              : p
          )
        );
      }
    }

    return response;
  };

  const handleFetchPostComments = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostComments/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postComments.comments; // Return the likes for a given post
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const [tPostDetails, setTPostDetails] = useState([]);

  const trendingPostDetailsFunc = async () => {
    const posts = await handleFetchTrendingPosts();
    const postsWithDetails = await Promise.all(
      posts.map(async (post) => {
        const likes = await handleFetchPostLikes(post._id);
        const comments = await handleFetchPostComments(post._id);
        let likeStatus = likes.some((like) => like.user === parsedId)
          ? "Liked"
          : "Not Liked";

        return { ...post, likes, comments, likeStatus };
      })
    );
    setTPostDetails(postsWithDetails);
  };

  const [specificPostComments, setSpecificPostComments] = useState([]);
  const handleSpecificPostComments = (id) => {
    const post = tPostDetails.find((post) => post._id === id);
    setSpecificPostComments(post.comments);
  };
  const [cText, setCText] = useState("");
  const handleCommentOnChange = (e) => {
    setCText(e.target.value);
  };
  const handleAddComment = async (postId, text, event) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    if (event.key === "Enter" || event.key === "enter") {
      setCText("");
      try {
        const response = await fetch(
          `${ENDPOINT}/api/post/commentOnPost/${postId}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              "auth-token": parsedToken,
            },
            body: JSON.stringify({
              text,
            }),
          }
        );
        const data = await response.json();
        if (data.success) {
          setSpecificPostComments(data.updatedComment.comments);
          setTPostDetails(
            tPostDetails.map((p) =>
              p._id === postId
                ? {
                    ...p,
                    comments: data.updatedComment.comments,
                  }
                : p
            )
          );
        }
      } catch (error) {
        console.log("Error:", error.message);
      }
    }
  };

  const [specificPostId, setSpecificPosId] = useState("");

  const handleStoreSpecificPostId = (id) => {
    setSpecificPosId(id);
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-6">
      {/* Search Section */}
      {console.log(tPostDetails)}

      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-6">
        <h1 className="text-3xl font-bold">Explore</h1>
        <div className="mt-4">
          <input
            type="text"
            value={keyword}
            onChange={handleOnchange}
            placeholder="Search for posts, people, or hashtags"
            className="w-full px-4 py-2 rounded-lg shadow-md bg-white text-gray-800"
          />
        </div>
      </div>
      <div className="max-h-[90vh] overflow-y-auto">
        {!show &&
          searchUsers &&
          searchUsers.map((user) => {
            return (
              <div
                className="flex items-center p-2 bg-gray-50 rounded-lg shadow-md text-black hover:bg-gray-200 transition-all duration-300 cursor-pointer border-black border-b-2 mb-3 "
                key={user._id}
                onClick={() => handleFetchSearchedUData(user._id)}
              >
                <img
                  src={user.picture}
                  alt="Failed to load img"
                  className="w-16 h-16 ml-2 object-cover rounded-full border-2 border-gray-800"
                />
                <div className="ml-4">
                  <h2 className="text-lg font-semibold ">{user.fullName}</h2>
                  <p className="text-sm ">@{user.userName}</p>
                </div>
              </div>
            );
          })}
        {!show && !searchUsers ? (
          <p className="text-lg text-center text-gray-500 font-medium py-4">
            {" "}
            No users found. Try searching with a different keyword!
          </p>
        ) : (
          ""
        )}
      </div>

      {/* Trending Section */}
      {/* <div className="mb-8">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Trending</h2>
        <div className="flex space-x-6 overflow-x-auto">
          {["#Nature", "#Tech", "#Fitness", "#Travel", "#Food"].map(
            (tag, index) => (
              <div
                key={index}
                className="bg-gray-200 px-4 py-2 rounded-full text-sm text-gray-700 hover:bg-blue-500 hover:text-white cursor-pointer"
              >
                {tag}
              </div>
            )
          )}
        </div>
      </div> */}

      {show && (
        <>
          {/* Recommended Posts Section */}
          <div className="space-y-8">
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">
              Trending Posts
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3  gap-6">
              {tPostDetails.map((post) => (
                <div
                  key={post.id}
                  className="bg-white rounded-xl shadow-lg overflow-hidden transition-transform transform hover:scale-105 duration-300 ease-in-out border border-gray-200"
                >
                  {post.mediaType === "image" ? (
                    <img
                      src={post.media}
                      alt="Failed to load media"
                      className="w-full h-60 object-cover"
                    />
                  ) : (
                    <video
                      src={post.media}
                      alt="Failed to load media"
                      className="w-full h-60 object-cover"
                      controls
                      muted
                    />
                  )}
                  {/* Post Image */}

                  <div className="p-6">
                    {/* User Info */}
                    <div className="flex items-center space-x-4 mb-4">
                      <img
                        src={post.userId.picture}
                        alt="Failed to load img"
                        className="w-12 h-12 rounded-full object-cover border-2 border-blue-500"
                      />
                      <div>
                        <p className="font-bold text-lg text-gray-900">
                          {post.userId.userName}
                        </p>
                        <p className="text-sm text-gray-500">
                          {new Date(post.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>

                    {/* Post Description */}
                    <p className="text-gray-700 mb-4 leading-relaxed">
                      {post.description}
                    </p>

                    {/* Interaction Buttons */}
                    <div className="flex justify-between items-center border-t pt-4">
                      <button className="flex items-center space-x-2 text-gray-600 hover:text-red-500">
                        <FontAwesomeIcon
                          icon={
                            post.likeStatus === "Liked" ? LikeHeart : faHeart
                          }
                          className={`text-2xl cursor-pointer ${
                            post.likeStatus === "Liked"
                              ? "text-red-600"
                              : "text-gray-600"
                          } hover:scale-110 transition-transform`}
                          onClick={() => handleLikePost(post._id)}
                        />
                        <span className="text-lg font-semibold">
                          {post.likeCount}
                        </span>
                      </button>

                      <button className="flex items-center space-x-2 text-gray-600 hover:text-blue-500">
                        <FontAwesomeIcon icon={faComment} size="lg" />
                        <span className="text-lg font-semibold">
                          {post.commentCount}
                        </span>
                      </button>

                      <button className="flex items-center space-x-2 text-gray-600 hover:text-green-500">
                        <FontAwesomeIcon icon={faShareSquare} size="lg" />
                        <span className="text-lg font-semibold">
                          {post.shares} Shares
                        </span>
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Explore;
