import React, { useEffect, useState } from "react";
import Signup from "./Signup";
import Login from "./Login";

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const [websiteName, setWebsiteName] = useState("");

  useEffect(() => {
    const name = process.env.REACT_APP_WEBSITE_NAME;
    let index = 0;
    let direction = 1;

    setInterval(() => {
      setWebsiteName(name.slice(0, index));
      index += direction;

      if (index > name.length) {
        direction = -1;
        index = name.length;
      } else if (index < 0) {
        direction = 1;
        index = 0;
      }
    }, 300);
  }, []);

  return (
    <div className="authPageBG">
      <div className="min-h-screen flex justify-center items-center">
        <form className="border-2 max-h-[90vh] overflow-y-auto border-black bg-white min-w-[70vw] md:min-w-[40vw] rounded-lg p-5">
          <div className="flex justify-center mt-3 h-10">
            <h3 className="custom-font text-4xl text-black font-bold">
              {websiteName}
            </h3>
          </div>
          <div role="tablist" className="tabs mt-2 flex">
            <button
              type="button"
              role="tab"
              className={`tab w-1/2 h-10 text-center text-xl rounded-sm ${
                activeTab === "tab1"
                  ? "bg-blue-500 text-white"
                  : "bg-gray-200 text-gray-700"
              }`}
              aria-selected={activeTab === "tab1"}
              onClick={() => setActiveTab("tab1")}
            >
              Signup
            </button>
            <button
              type="button"
              role="tab"
              className={`tab w-1/2 h-10 text-center text-xl rounded-sm ${
                activeTab === "tab2"
                  ? "bg-blue-500 text-white"
                  : "bg-gray-200 text-gray-700"
              }`}
              aria-selected={activeTab === "tab2"}
              onClick={() => setActiveTab("tab2")}
            >
              Login
            </button>
          </div>
          <div className="mt-5">
            {activeTab === "tab1" && (
              <div role="tabpanel">
                <Signup />
              </div>
            )}
            {activeTab === "tab2" && (
              <div role="tabpanel">
                <Login />
              </div>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default AuthPage;
