import React, { useState } from "react";

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState("tab1"); // Initializing the active tab
  return (
    <>
      <div className="authPageBG">
        <div className="min-h-screen flex justify-center items-center">
          <form className="border-2 border-black bg-white min-w-[70vw] md:min-w-[40vw] rounded-lg p-5">
            <div className="flex justify-center mt-3">
              <h3 className="custom-font text-4xl text-black font-bold">
                Vividly
              </h3>
            </div>
            <div role="tablist" className="tabs tabs-bordered mt-4 flex">
              <button
                type="button"
                role="tab"
                className={`tab w-1/2 text-center py-2 ${
                  activeTab === "tab1"
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200"
                }`}
                aria-selected={activeTab === "tab1"}
                onClick={() => setActiveTab("tab1")}
              >
                Tab 1
              </button>
              <button
                type="button"
                role="tab"
                className={`tab w-1/2 text-center py-2 ${
                  activeTab === "tab2"
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200"
                }`}
                aria-selected={activeTab === "tab2"}
                onClick={() => setActiveTab("tab2")}
              >
                Tab 2
              </button>
            </div>
            <div className="mt-5">
              {activeTab === "tab1" && (
                <div role="tabpanel" className="p-10">
                  <p>This is the content for Tab 1.</p>
                </div>
              )}
              {activeTab === "tab2" && (
                <div role="tabpanel" className="p-10">
                  <p>This is the content for Tab 2.</p>
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default AuthPage;
