import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation } from "react-router-dom";
import {
  faUser,
  faUsers,
  faCalendar,
  faShoppingBag,
  faTachometerAlt,
  faUserCircle,
  faCreditCard,
  faBars,
  faThumbsUp,
  faCog,
} from "@fortawesome/free-solid-svg-icons";

const Sidebar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const sidebarRef = useRef(null);

  // Function to handle opening and closing of the sidebar
  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Handle ESC key to close sidebar
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "Escape" && sidebarOpen) {
        setSidebarOpen(false);
      }
    };

    // Add event listener for ESC key
    document.addEventListener("keydown", handleKeyDown);

    // Cleanup event listener on component unmount
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [sidebarOpen]);

  // Handle click outside the sidebar to close it
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
        setSidebarOpen(false); // Close the sidebar if click is outside
      }
    };

    // Add event listener to handle clicks outside the sidebar
    document.addEventListener("mousedown", handleClickOutside);

    // Cleanup event listener on component unmount
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <>
      {/* Button Show on only mobile devices to toggle sidebar */}
      <button
        className="sm:hidden pt-20 pl-5"
        aria-label="Open Sidebar"
        onClick={handleSidebarToggle}
      >
        <FontAwesomeIcon
          icon={faBars}
          className="text-2xl bg-black text-white p-2 rounded-lg"
        />
      </button>
      {/* Siderbar for large devices */}
      <aside className="hidden sm:block sm:w-64 h-screen bg-gray-100 border-r fixed top-0 left-0 pt-20">
        <ul className="space-y-7 p-2 sm:p-6">
          <Link to="/settings">
            <SidebarItem icon={faCog} text="Settings" path="/settings" />
          </Link>
          <Link to="/profile" className="block">
            <SidebarItem icon={faUserCircle} text="Profile" path="/profile" />
          </Link>
          <Link to="/dashboard" className="block">
            <SidebarItem
              icon={faTachometerAlt}
              text="Dashboard"
              path="/dashboard"
            />
          </Link>
          <Link to="/groups" className="block">
            <SidebarItem icon={faUsers} text="Groups" path="/groups" />
          </Link>
          <Link to="/friends" className="block">
            <SidebarItem icon={faUser} text="Friends" path="/friends" />
          </Link>
          <Link to="/events" className="block">
            <SidebarItem icon={faCalendar} text="Events" path="/events" />
          </Link>
          <Link to="/marketplace" className="block">
            <SidebarItem
              icon={faShoppingBag}
              text="Marketplace"
              path="/marketplace"
            />
          </Link>

          <Link to="/subscriptions" className="block">
            <SidebarItem
              icon={faCreditCard}
              text="Subscriptions"
              path="/subscriptions"
            />
          </Link>
          <Link to="/likes" className="block">
            <SidebarItem icon={faThumbsUp} text="Liked Posts" path="/likes" />
          </Link>
        </ul>
      </aside>
      {/* Sidebar for Mobile Devices */}
      <div
        ref={sidebarRef}
        className={`fixed mt-16 inset-0 w-64 sm:hidden bg-gray-800 bg-opacity-50 z-40   transition-transform duration-300 transform ${
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        }`}
        aria-label="Sidebar Navigation"
        role="navigation"
      >
        <div className="w-64 bg-white h-full shadow-lg fixed z-50 p-6">
          <ul className="space-y-7 p-2 sm:p-6">
            <Link to="/profile" className="block" onClick={handleSidebarToggle}>
              <SidebarItem icon={faUserCircle} text="Profile" path="/profile" />
            </Link>
            <Link
              to="/dashboard"
              className="block"
              onClick={handleSidebarToggle}
            >
              <SidebarItem
                icon={faTachometerAlt}
                text="Dashboard"
                path="/dashboard"
              />
            </Link>
            <Link to="/groups" className="block" onClick={handleSidebarToggle}>
              <SidebarItem icon={faUsers} text="Groups" path="/groups" />
            </Link>
            <Link to="/friends" className="block" onClick={handleSidebarToggle}>
              <SidebarItem icon={faUser} text="Friends" path="/friends" />
            </Link>
            <Link to="/events" className="block" onClick={handleSidebarToggle}>
              <SidebarItem icon={faCalendar} text="Events" path="/events" />
            </Link>
            <Link
              to="/marketplace"
              className="block"
              onClick={handleSidebarToggle}
            >
              <SidebarItem
                icon={faShoppingBag}
                text="Marketplace"
                path="/marketplace"
              />
            </Link>

            <Link
              to="/subscriptions"
              className="block"
              onClick={handleSidebarToggle}
            >
              <SidebarItem
                icon={faCreditCard}
                text="Subscriptions"
                path="/subscriptions"
              />
            </Link>
            <Link to="/likes" className="block" onClick={handleSidebarToggle}>
              <SidebarItem icon={faThumbsUp} text="Liked Posts" path="/likes" />
            </Link>
          </ul>
        </div>
      </div>
    </>
  );
};

const SidebarItem = ({ icon, text, path }) => {
  const location = useLocation();
  const isActive = location.pathname === path;

  return (
    <li
      className={`flex items-center space-x-3 ${
        isActive ? "activeLink" : "text-gray-700"
      } hover:text-blue-500 cursor-pointer`}
    >
      <FontAwesomeIcon icon={icon} className="w-5 h-5" />
      <span>{text}</span>
    </li>
  );
};

export default Sidebar;
