import React, { useState, useEffect } from "react";

const SkeletonLoader = () => {
  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {/* Search Bar Skeleton */}
      <div className="bg-blue-600 p-4 rounded-lg">
        <div className="h-10 w-full bg-gray-300 animate-pulse rounded-lg"></div>
      </div>

      {/* User Grid Skeleton */}
      <div className="p-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {/* Skeleton for each user */}
        {[...Array(4)].map((_, index) => (
          <div
            key={index}
            className="relative bg-white p-4 rounded-lg shadow-lg animate-pulse"
          >
            {/* Skeleton Profile Picture */}
            <div className="w-full h-40 mb-4 bg-gray-300 rounded-lg"></div>

            {/* Skeleton User Info */}
            <div className="text-center">
              <div className="h-4 w-24 bg-gray-300 rounded-lg mb-2"></div>
              <div className="h-3 w-36 bg-gray-300 rounded-lg"></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SkeletonLoader;
