import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import SkeletonLoader from "./SkeletonLoader";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart,
  faComment,
  faShareSquare,
} from "@fortawesome/free-regular-svg-icons";
import { faHeart as LikeHeart } from "@fortawesome/free-solid-svg-icons";
const PostPreview = () => {
  const navigate = useNavigate();
  const [showSkeleton, setShowSkeleton] = useState(false);
  const [countLoading, setCountLoading] = useState(0);
  const theme = JSON.parse(localStorage.getItem("theme"));
  const context = useContext(vividlyContext);
  const [postData, setPostData] = useState("");
  const { fetchPostPreview } = context;

  const handlePostData = async () => {
    if (countLoading === 0) {
      setShowSkeleton(true);
    }
    const parsedPId = JSON.parse(localStorage.getItem("postPreview"));
    const response = await fetchPostPreview(parsedPId);
    setPostData(response);
    setCountLoading(1);
    setShowSkeleton(false);
  };
  const handSearchUser = (uId) => {
    localStorage.setItem("sUserId", JSON.stringify(uId));
    navigate("/searchUser");
  };

  useEffect(() => {
    handlePostData();
  }, []);

  const handleFetchPostLikes = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostLikes/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postLikes.likes;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleAddComment = async (postId, text, event) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    if (event.key === "Enter" || event.key === "enter") {
      setCText("");
      try {
        const response = await fetch(
          `${ENDPOINT}/api/post/commentOnPost/${postId}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              "auth-token": parsedToken,
            },
            body: JSON.stringify({
              text,
            }),
          }
        );
        const data = await response.json();
        if (data.success) {
          setSpecificPostComments(data.updatedComment.comments);
          setUserPost(
            userPost.map((p) =>
              p._id === postId
                ? {
                    ...p,
                    comments: data.updatedComment.comments,
                  }
                : p
            )
          );
        }
      } catch (error) {
        console.log("Error:", error.message);
      }
    }
  };


  return (
    <div
      className={`max-w-7xl mx-auto min-h-screen sm:pl-72 sm:pt-24 p-4 ${
        theme === "dark" ? "bg-gray-900" : "bg-white"
      }  shadow-lg rounded-lg overflow-hidden`}
    >
      {showSkeleton && <SkeletonLoader />}
      {!showSkeleton && (
        <>
          <div
            className={`flex items-center px-6 py-4 ${
              theme === "dark"
                ? "bg-gray-800 border border-gray-400"
                : "bg-gray-100"
            } rounded-lg mb-3`}
          >
            <img
              src={postData && postData.authordata.picture}
              alt="Creator Avatar"
              onClick={() => handSearchUser(postData.authordata._id)}
              className={`w-12 h-12 rounded-full ${
                theme === "dark" ? "border-2 border-gray-300" : ""
              } object-cover hover:cursor-pointer`}
            />
            <div className="ml-4">
              <h3
                className={`text-lg font-semibold hover:cursor-pointer ${
                  theme === "dark" ? "text-gray-200 font-bold" : "text-gray-800"
                } `}
                onClick={() => handSearchUser(postData.authordata._id)}
              >
                {postData && postData.authordata.fullName}
              </h3>
              <p
                className={`text-sm ${
                  theme === "dark" ? "text-gray-300" : "text-gray-500"
                } `}
              >
                @{postData && postData.authordata.userName}
              </p>
            </div>
          </div>

          <div className="relative">
            {postData && postData.findPost.mediaType === "video" ? (
              <video
                src={postData && postData.findPost.media}
                alt="Post Media"
                className={`w-full max-h-[85vh] object-contain rounded-lg border ${
                  theme === "black" ? "border-gray-200" : "border-gray-600"
                }`}
                controls
              />
            ) : (
              <img
                src={postData && postData.findPost.media}
                alt="Post Media"
                className={`w-full max-h-[85vh] object-contain rounded-lg border ${
                  theme === "black" ? "border-gray-200" : "border-gray-600"
                }`}
              />
            )}
          </div>

          <div className="p-6">
            <p
              className={`${
                theme === "dark" ? "text-gray-300" : "text-gray-700"
              }`}
            >
              Description: {postData && postData.findPost.description}
            </p>
          </div>

          <div className="flex justify-between items-center px-6 py-4 border-t border-gray-200">
            {/* Like and Comment Section */}
            <div className="flex items-center space-x-6">
              {/* Likes */}
              <div className="flex items-center space-x-2">
                <FontAwesomeIcon
                  icon={faHeart}
                  className={`cursor-pointer ${
                    theme === "dark"
                      ? "text-white hover:text-gray-400"
                      : "text-black hover:text-gray-600"
                  }`}
                  // onClick={handleLike} // Add like functionality here
                />
                <p
                  className={`${
                    theme === "dark" ? "text-gray-200" : "text-gray-600"
                  }`}
                >
                  {postData && postData.fetchLikes.likes.length} Likes
                </p>
              </div>
              {/* Comments */}
              <div className="flex items-center space-x-2">
                <FontAwesomeIcon
                  icon={faComment}
                  className={`cursor-pointer ${
                    theme === "dark"
                      ? "text-white hover:text-gray-400"
                      : "text-black hover:text-gray-600"
                  }`}
                />
                <p
                  className={`${
                    theme === "dark" ? "text-gray-200" : "text-gray-600"
                  }`}
                >
                  {postData && postData.fetchComments.comments.length} Comments
                </p>
              </div>
            </div>
          </div>

          {/* Comments Section */}
          <div className="p-6 border-t border-gray-200">
            <h4
              className={`text-lg font-semibold ${
                theme === "dark" ? "text-white" : "text-gray-800"
              } mb-4`}
            >
              Comments
            </h4>
            {/* Comment Input Field */}
            <div className="flex items-center space-x-4 mb-4">
              <input
                type="text"
                placeholder="Add a comment..."
                className={`flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring ${
                  theme === "dark"
                    ? "bg-gray-800 text-gray-200 border-gray-600 focus:ring-gray-400"
                    : "bg-gray-100 text-gray-800 border-gray-300 focus:ring-gray-200"
                }`}
                // value={newComment} // Bind to a state variable for new comment
                // onChange={(e) => setNewComment(e.target.value)} // Update the state
              />
              <button
                className={`px-4 py-2 rounded-lg ${
                  theme === "dark"
                    ? "bg-green-600 text-white hover:bg-green-500"
                    : "bg-green-500 text-white hover:bg-green-400"
                }`}
                // onClick={handleAddComment} // Add comment functionality
              >
                Add
              </button>
            </div>

            <div className="space-y-4 max-h-56 overflow-y-auto">
              {postData &&
                postData.fetchComments.comments.map((c) => {
                  return (
                    <div className="flex items-start space-x-4" key={c.id}>
                      <img
                        src={c.userPic}
                        alt="Commenter Avatar"
                        className={`w-10 h-10 hover:cursor-pointer ${
                          theme === "dark" ? "border border-white" : ""
                        } rounded-full object-cover`}
                        onClick={() => handSearchUser(c.user)}
                      />
                      <div>
                        <p
                          className={`hover:cursor-pointer ${
                            theme === "dark" ? "text-gray-200" : "text-gray-800"
                          } font-semibold`}
                          onClick={() => handSearchUser(c.user)}
                        >
                          {c.name}
                        </p>
                        <p
                          className={`${
                            theme === "dark" ? "text-gray-300" : "text-gray-700"
                          } text-sm`}
                        >
                          {c.text}
                        </p>
                      </div>
                    </div>
                  );
                })}
              {postData && postData.fetchComments.comments.length === 0 ? (
                <div
                  className={`${
                    theme === "dark" ? "text-gray-300" : "text-gray-800"
                  }`}
                >
                  No comments yet
                </div>
              ) : null}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default PostPreview;
