import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation } from "react-router-dom";

const ProfileDropdown = () => {
  const [open, setOpen] = useState(false);
  const dropDownRef = useRef(null);
  // Handle click outside the sidebar to close it
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropDownRef.current && !dropDownRef.current.contains(event.target)) {
        setOpen(false); // Close the sidebar if click is outside
      }
    };

    // Add event listener to handle clicks outside the sidebar
    document.addEventListener("mousedown", handleClickOutside);

    // Cleanup event listener on component unmount
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const closeDropDown = () => {
    setOpen(false);
  };

  return (
    <div className="relative">
      <div onClick={() => setOpen(!open)}>
        <FontAwesomeIcon
          icon="user"
          className="text-gray-700 esmd:w-5 esmd:h-5 sm:w-8 sm:h-8 cursor-pointer"
        />
      </div>

      {open && (
        <div
          className="absolute right-0 mt-2 w-48 bg-white text-black rounded-md shadow-lg z-50"
          ref={dropDownRef}
        >
          <ul>
            <Link to="/settings">
              <DropdownItem
                icon="cog"
                text="Settings"
                path="/settings"
                onClick={closeDropDown}
              />
            </Link>
            <Link to="/helpCenter">
              <DropdownItem
                icon="question-circle"
                text="Help Center"
                path="/helpCenter"
              />
            </Link>
            <Link to="/logout">
              <DropdownItem icon="sign-out-alt" text="Logout" path="/logout" />
            </Link>
          </ul>
        </div>
      )}
    </div>
  );
};

const DropdownItem = ({ icon, text, path }) => {
  const location = useLocation();
  const isActive = location.pathname === path;
  return (
    <li
      className={`flex items-center space-x-2 p-2 ${
        isActive ? "bg-blue-500 text-white" : ""
      } hover:bg-blue-500 hover:text-white cursor-pointer`}
    >
      <FontAwesomeIcon
        icon={icon}
        className="esmd:w-3 esmd:h-3 lmd: w-4 lmd: h-4 elmd:w-5 elmd:h-5"
      />
      <span className="text-[10px] lmd:text-xs sm:text-[16px]">{text}</span>
    </li>
  );
};

export default ProfileDropdown;
