            <div
              ref={storiesContainerRef}
              className="flex space-x-4 overflow-x-auto scrollbar-hide whitespace-nowrap mb-4 py-2"
            >
              {/* Story Items */}
              {Array(12)
                .fill("")
                .map((_, index) => (
                  <div
                    key={index}
                    className="flex-shrink-0 w-24 h-24 bg-gray-300 rounded-full cursor-pointer"
                  >
                    <img
                      src={`https://via.placeholder.com/150?text=Story+${
                        index + 1
                      }`}
                      alt={`Story ${index + 1}`}
                      className="w-full h-full rounded-full object-cover"
                    />
                  </div>
                ))}
            </div>

            {/* Scroll Indicators */}
            {showPrevIcon && (
              <div
                className="absolute top-1/2 left-4 transform -translate-y-1/2 flex items-center justify-center w-8 h-8 rounded-full bg-black text-white cursor-pointer"
                onClick={() => scrollStories("prev")}
              >
                <FontAwesomeIcon icon={faAngleLeft} />
              </div>
            )}
            {showNextIcon && (
              <div
                className="absolute top-1/2 right-4 transform -translate-y-1/2 flex items-center justify-center w-8 h-8 rounded-full bg-black text-white cursor-pointer"
