import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faComment } from "@fortawesome/free-regular-svg-icons";
import { Link } from "react-router-dom";

const Home = () => {
  const [showPrevIcon, setShowPrevIcon] = useState(false);
  const [showNextIcon, setShowNextIcon] = useState(true);
  const storiesContainerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (storiesContainerRef.current) {
        const { scrollLeft, scrollWidth, clientWidth } =
          storiesContainerRef.current;

        // Show "prev" icon if scrollLeft > 0
        setShowPrevIcon(scrollLeft > 0);

        // Show "next" icon if there's more content to scroll
        setShowNextIcon(scrollLeft + clientWidth < scrollWidth - 1);
      }
    };

    // Attach scroll event listener
    const container = storiesContainerRef.current;
    container.addEventListener("scroll", handleScroll);

    // Initial state update
    handleScroll();

    return () => {
      // Cleanup event listener
      container.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const scrollStories = (direction) => {
    if (storiesContainerRef.current) {
      const scrollAmount = 150; // Adjust the scroll step as needed
      storiesContainerRef.current.scrollBy({
        left: direction === "next" ? scrollAmount : -scrollAmount,
        behavior: "smooth",
      });
    }
  };

  return (
    <>
      {/* Main Content Section */}
      <div className="flex min-h-screen sm:pl-72 sm:pt-24 max-w-7xl mx-auto sm:p-4">
        {/* Feed Section */}
        <div className="w-full bg-white p-4 rounded-lg shadow-lg">
          {/* Stories/Highlights */}
          {/* Stories Section with Scroll Indicator */}
          <div className="relative mb-6">
            <div
              ref={storiesContainerRef}
              className="flex space-x-4 overflow-x-auto scrollbar-hide whitespace-nowrap mb-4 py-2"
            >
              {/* Story Items */}
              {Array(12)
                .fill("")
                .map((_, index) => (
                  <div
                    key={index}
                    className="flex-shrink-0 w-24 h-24 bg-gray-300 rounded-full cursor-pointer"
                  >
                    <img
                      src={`https://via.placeholder.com/150?text=Story+${
                        index + 1
                      }`}
                      alt={`Story ${index + 1}`}
                      className="w-full h-full rounded-full object-cover"
                    />
                  </div>
                ))}
            </div>

            {/* Scroll Indicators */}
            {showPrevIcon && (
              <div
                className="absolute top-1/2 left-4 transform -translate-y-1/2 flex items-center justify-center w-8 h-8 rounded-full bg-black text-white cursor-pointer"
                onClick={() => scrollStories("prev")}
              >
                <FontAwesomeIcon icon="angle-left" />
              </div>
            )}
            {showNextIcon && (
              <div
                className="absolute top-1/2 right-4 transform -translate-y-1/2 flex items-center justify-center w-8 h-8 rounded-full bg-black text-white cursor-pointer"
                onClick={() => scrollStories("next")}
              >
                <FontAwesomeIcon icon="angle-right" />
              </div>
            )}
          </div>

          {/* Create Post Section */}
          <div className="mb-6">
            <textarea
              placeholder="What's on your mind?"
              className="w-full p-4 border rounded-lg bg-gray-50"
              rows="3"
            ></textarea>
            <button className="mt-4 bg-blue-500 text-white p-2 rounded-lg">
              Post
            </button>
          </div>

          {/* Feed Posts */}
          <div className="space-y-4">
            {/* Sample Post */}
            <div className="border-b pb-4">
              <div className="flex items-center space-x-2">
                <div className="w-12 h-12 rounded-full bg-gray-300"></div>
                <div>
                  <div className="font-semibold">User Name</div>
                  <div className="text-sm text-gray-500">2 hours ago</div>
                </div>
              </div>
              <p className="mt-2 text-gray-700">
                This is a sample post content...
              </p>
              <div className="mt-2 flex space-x-6">
                <div className="flex items-center space-x-1 text-gray-700">
                  <FontAwesomeIcon icon="thumbs-up" className="text-xl" />
                  <span>12</span>
                </div>
                <div className="flex items-center space-x-1 text-gray-700">
                  <FontAwesomeIcon icon="faComment" className="text-xl" />
                  <span>5</span>
                </div>
              </div>
            </div>
            {/* Add more posts here */}
            <div className="border-b pb-4">
              <div className="flex items-center space-x-2">
                <div className="w-12 h-12 rounded-full bg-gray-300"></div>
                <div>
                  <div className="font-semibold">User Name</div>
                  <div className="text-sm text-gray-500">2 hours ago</div>
                </div>
              </div>
              <p className="mt-2 text-gray-700">
                This is a sample post content...
              </p>
              <div className="mt-2 flex space-x-6">
                <div className="flex items-center space-x-1 text-gray-700">
                  <FontAwesomeIcon icon="thumbs-up" className="text-xl" />
                  <span>12</span>
                </div>
                <div className="flex items-center space-x-1 text-gray-700">
                  <FontAwesomeIcon icon="comment" className="text-xl" />
                  <span>5</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
