import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const AditionlaInfoSignup = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [gender, setGender] = useState("");
  const [error, setError] = useState(false);
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!gender) {
      setError(true); // Show error only when not selected
    } else {
      setError(false);
      alert(`Selected Gender: ${gender}`);
    }
  };

  return (
    <>
      <div className="grid sm:grid-cols-2 gap-x-4">
        <div>
          <label
            htmlFor="dob"
            className="block mb-1 text-sm font-bold text-gray-900 dark:text-white"
          >
            Date of Birth
          </label>
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 hover:cursor-pointer"
            required
            placeholderText="Select date"
            dateFormat="dd-MM-yyyy"
          />
        </div>
        <div className="mt-3 mb-4 sm:mt-0">
          <div className="flex items-center">
            <input
              type="radio"
              id="male"
              name="gender"
              value="male"
              checked={gender === "male"}
              onChange={(e) => setGender(e.target.value)}
              className="focus:ring-0"
            />
            <label
              htmlFor="male"
              className="ml-2 text-md font-medium text-gray-900 dark:text-white"
            >
              Male
            </label>
          </div>

          <div className="flex items-center">
            <input
              type="radio"
              id="female"
              name="gender"
              value="female"
              checked={gender === "female"}
              onChange={(e) => setGender(e.target.value)}
              className="focus:ring-0"
            />
            <label
              htmlFor="female"
              className="ml-2 text-md font-medium text-gray-900 dark:text-white"
            >
              Female
            </label>
          </div>
        </div>

        {error && (
          <p className="text-red-500 text-sm mt-2">Please select a gender.</p>
        )}

        <button
          type="submit"
          className="mt-4 bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition-all duration-300"
        >
          Submit
        </button>
      </div>
    </>
  );
};

export default AditionlaInfoSignup;
